templateSet.resultsTemplate:
{{#compare total '==' 0}}
<div class="alert alert-warning">
	<p>Couldn't find an organisation with that username on Good HQ</p>
</div>
{{/compare}}

{{#each results}}
<div class="mw-goodhq-result" id="mw-goodhq-result-{{Id}}">
	{{#if reviewcount}}
	<a href="https://goodhq.org"><img src="https://goodhq.org/images/good_hq_logo.png" /></a>
	<p>
		<a href="https://goodhq.org/{{xid}}">{{title}}</a> has {{reviewcount}} review{{#compare reviewcount '>' 1}}s{{/compare}} on Good HQ<br />
		<ul class="hearts">
            {{#compare stars '<' 0.25}}
                <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
            {{/compare}}
            {{#compare stars '>=' 0.25}}
                {{#compare stars '<' 0.75}}
                    <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
                {{#compare stars '>=' 0.75}}
                    <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
            {{/compare}}
            {{#compare stars '<' 1.25}}
                <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
            {{/compare}}
            {{#compare stars '>=' 1.25}}
                {{#compare stars '<' 1.75}}
                    <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
                {{#compare stars '>=' 1.75}}
                    <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
            {{/compare}}
            {{#compare stars '<' 2.25}}
                <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
            {{/compare}}
            {{#compare stars '>=' 2.25}}
                {{#compare stars '<' 2.75}}
                    <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
                {{#compare stars '>=' 2.75}}
                    <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
            {{/compare}}
            {{#compare stars '<' 3.25}}
                <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
            {{/compare}}
            {{#compare stars '>=' 3.25}}
                {{#compare stars '<' 3.75}}
                    <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
                {{#compare stars '>=' 3.75}}
                    <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
            {{/compare}}
            {{#compare stars '<' 4.25}}
                <li class="empty"><span aria-hidden="true" class="icon-heart"></span></li>
            {{/compare}}
            {{#compare stars '>=' 4.25}}
                {{#compare stars '<' 4.75}}
                    <li class="half"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
                {{#compare stars '>=' 4.75}}
                    <li class="filled"><span aria-hidden="true" class="icon-heart"></span></li>
                {{/compare}}
            {{/compare}}
		</ul>
		<a href="https://goodhq.org/{{xid}}/review">Share your story</a> |
		<a href="https://goodhq.org/{{xid}}?mode=review">Read review{{#compare reviewcount '>' 1}}s{{/compare}}</a>
	</p>
	{{else}}
	<p>
		<a href="https://goodhq.org/{{xid}}">{{title}}</a> is on
	</p>
	<a href="https://goodhq.org"><img src="https://goodhq.org/images/good_hq_logo.png" /></a>
	<p>
		Be the first person to<br />
		<strong><a href="https://goodhq.org/{{xid}}/review">share your story</a></strong>
	</p>
	{{/if}}
</div>
{{/each}}

{{#compare total '>' 0}}
<div class="alert alert-info">
	Number of results: {{total}} | Page {{currentPage}} of {{totalPages}}
</div>
{{else}}
<div class="alert alert-warning">
	<p>No results</p>
</div>
{{/compare}}

{{#each results}}
	<div class="mw-opportunities-result" id="mw-opportunities-result-{{Id}}">
		<h3>
			<a href="#mw-opportunities-{{Id}}">
				{{title}}
				{{#if organisation_name}}
					| {{organisation_name}}
				{{/if}}
				{{#each ../sorts}}
					({{round this 2}} miles)
				{{/each}}
			</a>
		</h3>

		<ul class="list-inline">
			{{#if milo_ref}}
			<li>
				<i class="fa fa-fw fa-book"></i> <strong>Ref:</strong> {{milo_ref}}
			</li>
			{{/if}} {{#if start_date}}
			<li>
				<i class="fa fa-fw fa-calendar"></i> <strong>Start date:</strong> {{start_date}}
			</li>
			{{/if}} {{#if work_type}}
			<li>
				<i class="fa fa-fw fa-briefcase"></i> <strong>Work type:</strong> {{work_type}}
			</li>
			{{/if}} {{#if client_group}}
			<li>
				<i class="fa fa-fw fa-users"></i> <strong>Client group:</strong> {{client_group}}
			</li>
			{{/if}} {{#if organisation_charity_number}}
			<li>
				<i class="fa fa-fw fa-hashtag"></i> <strong>Charity number:</strong> {{organisation_charity_number}}
			</li>
			{{/if}}
		</ul>

		<p>
			{{#if summary}} {{truncate summary 255 '[...]'}} {{else}} No summary given. {{/if}}
		</p>
	</div>
{{/each}}

{{#compare total '>' 0}}
<div class="alert alert-info">
	Number of results: {{total}} | Page {{currentPage}} of {{totalPages}}
</div>
{{else}}
<div class="alert alert-warning">
	<p>No results</p>
</div>
{{/compare}}

{{#each results}}
	<div class="mw-opportunities-result" id="mw-opportunities-result-{{Id}}">
		<h3>
			<a href="#mw-opportunities-{{Id}}">
				<strong>{{title}}</strong>
				{{#if organisation_name}}
					| {{organisation_name}}
				{{/if}}
				{{#each ../sorts}}
					({{round this 2}} miles)
				{{/each}}
			</a>
		</h3>

		<ul class="list-inline">
			{{#if reference_code}}
			<li>
				<i class="fa fa-fw fa-book"></i> <strong>Ref:</strong> {{reference_code}}
			</li>
			{{/if}} {{#if start_date}}
			<li>
				<i class="fa fa-fw fa-calendar"></i> <strong>Start date:</strong> {{start_date}}
			</li>
			{{/if}} {{#if work_type}}
			<li>
				<i class="fa fa-fw fa-briefcase"></i> <strong>Work type:</strong> {{work_type}}
			</li>
			{{/if}} {{#if client_group}}
			<li>
				<i class="fa fa-fw fa-users"></i> <strong>Client group:</strong> {{client_group}}
			</li>
			{{/if}} {{#if organisation_charity_number}}
			<li>
				<i class="fa fa-fw fa-hashtag"></i> <strong>Charity number:</strong> {{organisation_charity_number}}
			</li>
			{{/if}}
		</ul>

		<p>
			{{#if summary}} {{truncate summary 255 '[...]'}} {{else}} No summary given. {{/if}}
		</p>
	</div>
{{/each}}

{{#compare total '>' 0}}
<div class="alert alert-info">
	Number of results: {{total}} | Page {{currentPage}} of {{totalPages}}
</div>
{{else}}
<div class="alert alert-warning">
	<p>No results</p>
</div>
{{/compare}}

{{#each results}}
	<div class="mw-organisation-result" id="mw-organisations-result-{{Id}}">
		<h3>
			<a href="#mw-organisations-{{Id}}">
				{{title}}
				{{#if registered_charity_number}}
					<small>({{registered_charity_number}})</small>
				{{/if}}
			</a>
		</h3>

		<p>
			{{#if aims_and_objectives}} {{truncate aims_and_objectives 255 '[...]'}} {{else}} No aims or objectives given. {{/if}}
		</p>
		<p>
			{{#any location_street location_area_town location_city_county location_postcode}}
				{{#if location_street}}{{location_street}}, {{/if}}
				{{#if location_area_town}}{{location_area_town}}, {{/if}}
				{{#if location_city_county}}{{location_city_county}}, {{/if}}
				{{#if location_postcode}}{{location_postcode}}{{/if}}
			{{else}}
				No address given
			{{/any}}
		</p>
	</div>
{{/each}}
